import base64
import os

# Encoded variables (Replace with actual values)
var1 = ""
var2 = ""
var3 = ""
var4 = ""

# Step 1: Concatenate the encoded parts
full_encoded_message = var1 + var2 + var3 + var4

print(full_encoded_message)

# Step 2: First Base64 decoding

decoded_once = base64.b64decode(full_encoded_message)

# Step 3: Determine the script's directory
script_dir = os.path.dirname(os.path.abspath(__file__))  # Gets the directory of the running script
image_path = os.path.join(script_dir, "decoded_image.png")  # Creates the full path for the image

# Step 4: Save as an image
with open(image_path, "wb") as image_file:
    image_file.write(decoded_once)

print("Image successfully decoded and saved as 'decoded_image.png'.")